const WEBAPP_URL = 'https://photoforge.base44.app';

export interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
}

export interface User {
  id: string;
  email: string;
  full_name: string;
  role: string;
}

/**
 * Validate access key with the backend
 * @param accessKey - 24 character access key from user
 * @returns User data if valid, error if invalid
 */
export async function validateAccessKey(accessKey: string): Promise<ApiResponse<User>> {
  try {
    console.log('🔑 Validating access key...');

    const response = await fetch(`${WEBAPP_URL}/api/functions/validate-key`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ accessKey }),
    });

    const data = await response.json();

    if (response.ok && data.success) {
      console.log('✅ Access key valid:', data.user.email);
      return {
        success: true,
        data: data.user,
      };
    } else {
      console.error('❌ Invalid access key:', data.error);
      return {
        success: false,
        error: data.error || 'Invalid access key',
      };
    }
  } catch (error) {
    console.error('❌ Validation error:', error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Network error',
    };
  }
}

/**
 * Get projects with authenticated access key
 * @param accessKey - Validated access key
 */
export async function getProjects(accessKey: string): Promise<ApiResponse<any[]>> {
  try {
    const response = await fetch(`${WEBAPP_URL}/api/entities/Project`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${accessKey}`,
      },
    });

    if (response.ok) {
      const projects = await response.json();
      return { success: true, data: projects };
    } else {
      return { success: false, error: 'Failed to load projects' };
    }
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Network error',
    };
  }
}

/**
 * Create a new project
 * @param accessKey - Validated access key
 * @param project - Project data
 */
export async function createProject(accessKey: string, project: any): Promise<ApiResponse<any>> {
  try {
    const response = await fetch(`${WEBAPP_URL}/api/functions/createProjectMobile`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        access_key: accessKey,
        project: project,
      }),
    });

    const data = await response.json();

    if (response.ok && data.success) {
      return { success: true, data: data.data };
    } else {
      return { success: false, error: data.error || 'Failed to create project' };
    }
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Network error',
    };
  }
}

