package com.photoforge.mobile.drone

import dji.sdk.sdkmanager.DJISDKManager
import dji.sdk.base.BaseProduct
import dji.v5.common.callback.CommonCallbacks

object DJIManager {
    var product: BaseProduct? = null

    fun initialize(context: Context, callback: (Boolean, String?) -> Unit) {
        DJISDKManager.getInstance().registerApp(context, object : DJISDKManagerCallback {
            override fun onRegisterSuccess() {
                callback(true, null)
            }
            override fun onRegisterFailure(error: String?) {
                callback(false, error)
            }
        })
    }

    fun connectDrone(callback: (Boolean) -> Unit) {
        product = DJISDKManager.getInstance().product
        callback(product != null)
    }

    fun getTelemetry(): DroneState {
        val flightController = product?.flightController
        return DroneState(
            latitude = flightController?.state?.aircraftLocation?.latitude ?: 0.0,
            longitude = flightController?.state?.aircraftLocation?.longitude ?: 0.0,
            altitude = flightController?.state?.altitude ?: 0.0,
            battery = product?.battery?.remainingChargeInPercent ?: 0,
            speed = flightController?.state?.velocityX ?: 0.0,
            isFlying = flightController?.state?.isFlying ?: false
        )
    }

    fun startMission(waypoints: List<Waypoint>, callback: (Boolean) -> Unit) {
        // Upload waypoints to drone
        val mission = WaypointMission()
        waypoints.forEach { wp ->
            mission.addWaypoint(Waypoint(wp.latitude, wp.longitude, wp.altitude))
        }

        product?.missionControl?.scheduleElement(mission)?.let { scheduler ->
            scheduler.start { error ->
                callback(error == null)
            }
        } ?: callback(false)
    }
}

