Polling Service
class ProcessingStatusPoller(
    private val apiService: ApiService,
    private val modelId: String,
    private val accessKey: String
) {
    private var pollingJob: Job? = null
    private val _statusFlow = MutableStateFlow<ProcessingStatus?>(null)
    val statusFlow: StateFlow<ProcessingStatus?> = _statusFlow.asStateFlow()

    fun startPolling() {
        pollingJob?.cancel()
        pollingJob = CoroutineScope(Dispatchers.IO).launch {
            while (isActive) {
                try {
                    val response = apiService.checkProcessingStatus(
                        CheckProcessingRequest(accessKey, modelId)
                    )

                    if (response.success && response.data != null) {
                        _statusFlow.value = response.data

                        // Stop polling if processing complete or failed
                        if (!response.data.should_continue_polling) {
                            Log.d("Poller", "Processing complete, stopping poll")
                            stopPolling()
                            break
                        }

                        // Wait for poll_interval (1000ms = 1 second)
                        delay(response.data.poll_interval)
                    } else {
                        Log.e("Poller", "Failed to get status: ${response.error}")
                        delay(2000) // Retry after 2 seconds on error
                    }
                } catch (e: Exception) {
                    Log.e("Poller", "Polling error: ${e.message}")
                    delay(2000) // Retry after 2 seconds on error
                }
            }
        }
    }

    fun stopPolling() {
        pollingJob?.cancel()
        pollingJob = null
    }
}
